INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (4971, '方维仪', 'http://pics.shicimingju.com/zz/4708.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '方维仪（1585—1668），女，字仲贤，安徽桐城人（今桐城市区凤仪里人）。明末著名女诗人、画家，所作有《清芬阁集》，又辑历代妇女作品为《宫闺诗史》。其绘画师法宋代李公麟，尤擅长绘释道人物。特别是白描《观音大士图》形神兼备，许多人争相收藏。', null, '2首', '清', '2020-01-05 10:53:03');
