INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (4975, '吴绡', null, '吴绡，活动于清<id=108>顺治</id>（1644～1661）前后。字素公，又字片霞，号冰仙，长洲（今江苏苏州）人。通判吴水苍女，常熟进士<id=1766>许瑶</id>妻。善琴，工书画诗词，诗词清丽婉约，花卉钩勒设色俱佳，兰竹有生趣，与<id=4976>沈宛君</id>齐名。时人传云：“吴中闺秀徐小淑能诗文，<id=4977>赵瑞</id>容善画，有盛誉，惟夫人兼此二长。” 由于其夫<id=1766>许瑶</id>的关系，吴绡问学于“海虞二冯”的<id=1249>冯班</id>，又因父亲科第关系，称嘉善著名词人<id=30>柳州</id>词派领袖曹尔堪为年伯，而其父吴水苍与<id=365>吴伟业</id>联宗，故吴绡诗集中多有与梅村唱酬之作，称伟业为兄。 吴绡著《啸雪庵诗余》，存词四十四首，风格清丽婉约。', null, '4首', '清', '2020-01-05 10:53:07');
