INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (4978, '侯怀风', null, '侯怀风约活动于清<id=108>顺治</id>（1644～1661）前后，字若英。江苏嘉定（今属上海）人。侯峒曾女，侯蓁宜从妹。峒曾刚正不挠，为抗清义士，坚守嘉定十余日，城破，峒曾与其两子同投水殉国。故怀风诗多伤感昔，深沉悲壮。', null, '1首', '清', '2020-01-05 10:53:11');
