INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (4980, '倪仁吉', null, '倪仁吉(1607—1685)，女，明末清初人，字心蕙，自号凝香子，博通经史诗文，兼工书画刺绣。诗歌代表作品《山居四时杂咏》，绘画方面精通山水、人物、花卉、翎毛、走兽等创作，尤其善画美人，同时代人称“得其书画片羽者，皆珍若拱璧”。此外倪仁吉最出色的成就在于刺绣，因其擅长书画，故能结合水墨画法，善以绣代笔，活用针法，从而使刺绣生动逼真且难觅针线痕迹。', null, '1首', '清', '2020-01-05 10:53:13');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (4981, '范姝', null, '范姝，清女诗人。字洛仙，如皋人，诗人范献重侄女，同邑诸生李延公妻。有《贯月舫集》问世。', null, '1首', '清', '2020-01-05 10:53:14');
