INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5003, '聂绀弩', 'http://pics.shicimingju.com/zz/4761.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '聂绀弩（1903-1986）新中国著名诗人、散文家、“20世纪最大的自由主义者”（<id=374>周恩来</id>戏语），湖北京山人。曾用笔名耳耶、二鸦、箫今度等。聂绀弩的诗作新奇而不失韵味、幽默而满含辛酸，被称作“独具一格的散宜生体”。1986年于北京病逝。', null, '49首', '现代', '2020-01-05 10:53:38');
