INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5006, '金武祥', null, '金武祥（1841——1924）清末藏书家、诗人，原名则仁，字溎生，号粟香，又号菽香，别署一厈山人、水月主人等，江苏常州府江阴县（今江阴市）前周镇大岸村（现属璜土镇）人。江阴金氏明永乐年间由常州迁徙至江阴永陵乡。明启祯年间由大阚迁居大岸村。', null, '1首', '清', '2020-01-05 10:59:34');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5007, '李秉礼', null, '李秉礼（1748-1830），字敬之，号耕云、松甫、韦庐，又号七松老人。祖籍江西省临川县温圳杨溪村（今属进贤县温圳），寄籍广西桂林。清代著名诗人。官刑部江苏司郎中。诰授中宪大夫，驰封光禄大夫，与父李宜民、子李宗瀚被称为“李氏三代红顶子”。有《韦庐诗》内集、外集。', null, '1首', '清', '2020-01-05 10:59:35');
