INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5110, '陈洵', null, '陈洵，字述叔，别号海绡，是广东江门市潮连芝山人（前属新会县潮连乡），生于清朝同治十年（1871年）。', null, '1首', '近代', '2020-01-05 13:18:23');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5111, '冯煦', null, '冯煦（1842～1927）原名冯熙，字梦华，号蒿庵，晚号蒿叟、蒿隐。江苏金坛五叶人。少好词赋，有江南才子之称。<id=278>光绪</id>八年(1882)举人，<id=278>光绪</id>十二年（1886）进士，授翰林院编修。历官安徽凤阳府知府、四川按察使和安徽巡抚。辛亥革命后，寓居上海，以遗老自居。曾创立义赈协会，承办江淮赈务，参与纂修《江南通志》。冯煦工诗、词、骈文，尤以词名，著有《蒿庵类稿》等。', null, '1首', '近代', '2020-01-05 13:18:24');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5112, '庄棫', null, '庄棫（1830——1878），字中白，一字利叔，清代词人，学者，号东庄，又号蒿庵。丹徒人，生于道光十年（1830）。<id=278>光绪</id>四年（一八七八）卒。享年四十九岁。著有《蒿庵遗稿》，词甲、乙稿及补遗附焉。棫自序谓：“向从北宋溯五代十国，今复下求南宋得失离合之故”，足见其词学渊源所自。与<id=1210>谭献</id>齐名。<id=871>朱孝臧</id>合题二家词集云：“皋文说，沆瀣得庄、谭。感遇霜飞怜镜子，会心衣润费炉烟，妙不著言诠。”（《彊村语业》卷三）据此，知二氏固常州派之后劲也。', null, '6首', '清', '2020-01-05 13:18:25');
