INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5185, '笪重光', null, '笪重光（1623-1692），清朝书画家。字在辛，号君宜，又号蟾光、逸叟、江上外史、郁冈扫叶道人，晚年居茅山学道改名传光、蟾光，亦署逸光，号奉真、始青道人，江苏省句容人。一江苏丹徒人作。<id=108>顺治</id>九年进士，官御史，巡按江西，以劾明珠去官。罢官归乡，隐居茅山之麓，学导引，读丹书，潜心于道教。卒年七十。笪重光工书善画，与姜宸英、<id=5186>汪士鋐</id>、何焯称四大家。精古文辞。有《书筏》、《画筌》传世。', null, '1首', '清', '2020-01-05 13:37:00');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5186, '汪士鋐', null, '汪士鋐（1658～1723），清书法家、藏书家。字文升，号退谷，又号秋泉居士。长洲（今江苏苏州）人。<id=4873>汪琬</id>从子。', null, '1首', '清', '2020-01-05 13:37:00');
