INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5190, '毛德琦', null, '毛德琦，生卒年月不详，号心斋,清代浙江鄞县人,<id=41>康熙</id>五十三年任星子知县。', null, '1首', '清', '2020-01-05 13:37:05');
