INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5194, '闵麟嗣', null, '闵麟嗣，（公元1628～1704年），字宾连，号橄庵，徽州岩寺镇人（今安徽歙县）。著名明末清初学者、旅行家。', null, '1首', '清', '2020-01-05 13:37:09');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5195, '曹龙树', null, '曹龙树，字松龄，号星湖。江西星子人。清高宗<id=207>乾隆</id>36年（1771年）举人。历任江苏、沛县、桃源（今泗阳县）、如皋等知县，江宁（今南京市）、江防同知，江南（辖今上海市、江苏、安徽、江西婺源县地）乡试同考官。著有《星湖诗集》、《养云精舍文集》《星湖如皋攀辕集》等。', null, '2首', '清', '2020-01-05 13:37:11');
