INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5237, '夏征农', null, '夏征农（1904.01.31～2008.10.04）江西丰城市人，夏光庭（夏远）40世孙。曾求学于金陵大学（金陵大学1952年并入南京大学）中文系（现南京大学中文系）、复旦大学。中国共产党员。曾任新四军军政治部民运部部长、中共苏中四地委宣传部部长、苏中公学校长、苏中建设大学副校长、中共济南市委宣传部部长。建国后，任中共济南市委副书记，中共中央山东分局宣传部部长，中共山东省委常委、副书记，济南市委书记处书记，中共中央华东局宣传部部长，中共上海市委书记处书记，中共中央顾问委员会委员，上海市社联、文联主席，复旦大学党委书记，上海大学首席顾问，《中国大百科全书》总编辑委员会，《辞海》，《大辞海》主编。', null, '1首', '当代', '2020-01-05 13:37:57');
