INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5250, '郑珍', null, '郑珍（1806～1864）清代官员、学者。字子尹，晚号柴翁，别号子午山孩、五尺道人、且同亭长，贵州遵义人。道光十七年举人，选荔波县训导，咸丰间告归。同治初补江苏知县，未行而卒。 学宗许郑，治经学、小学，亦工书善画，还是晚清宋诗派作家，其诗风格奇崛，时伤艰涩，与独山莫友芝并称“西南巨儒”。所著有《仪礼私笺》、《说文逸字》、《说文新附考》、《巢经巢经说》、《郑学录》等。', null, '1首', '清', '2020-01-05 13:38:11');
