INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5289, '吴麐', null, '吴麐，一、[清]（一六九一―一七七二）字栗园，一作栗原，一字荛圃，安徽歙县籍，家江西景德镇。二、[清]字仁趾，天都（今安徽歙县）右姓，隶籍广陵（今江苏扬州）。', null, '1首', '清', '2020-01-05 13:38:54');
