INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5308, '余楍', null, '余楍，字生生，号钝蓭，蒙古族，祖籍四川省青神县（今四川省乐山市夹江县青州乡金星村），明万历三十四年丙午（1606年）生于明朝留都南京（今江苏南京），<id=41>康熙</id>二十四年乙丑(1685年)夏卒于江苏扬州城东琼花观。明诸生，世袭锦衣卫。明亡后，寓鄞之西湖上（今浙江宁波）。其所居名借鉴楼，结“七子诗社”，著有《增益轩诗草》、《遣愁集》等作品传于世。', null, '2首', '清', '2020-01-05 13:39:14');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (5309, '李邺嗣', null, '李邺嗣（1622－1680），原名文胤，也作文允，字邺嗣，又字淼亭，以字行，号杲堂，自号东洲遗老。鄞县（今浙江省宁波鄞州区）人，宋忠襄公<id=142>李显</id>忠之后。十二岁能诗，十六为诸生。', null, '21首', '清', '2020-01-05 13:39:15');
