INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (12993, '邹迪光', null, '邹迪光（1550——1626）字彦吉，号愚谷。明无锡人。万历二年进士。', null, '8首', '明', '2020-01-06 00:29:01');
