INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (13012, '蒋彝', null, '蒋彝（ChiangYee）（1903年—1977年），画家、诗人、作家、书法家。“中国文化的国际使者”。江西九江县人。由于他对中西文化交流所作出的贡献，受到西方人的尊敬，在英国被选为英国皇家艺术学会会员；收入五十年代英国编纂的《世界名人辞典》；是美国哥伦比亚大学终身教授；先后获美国赫复斯大学、长岛大学、香港大学、澳大利亚堪培拉国立大学颁赠的荣誉博士学位；被选为美国科学院艺术学院院士。此外，他还翻译了Coca-Cola的中文名可口可乐，使可口可乐原中文名蝌蝌啃蜡变身成为迄今为止被广告界公认为翻译得最好的品牌名可口可乐。南京大学著名校友。', null, '1首', '近代', '2020-01-06 00:29:21');
